export async function POST(request: NextRequest) {
  try {
    const { question, chatHistory } = await request.json();

    if (!question) {
      return new Response(
        JSON.stringify({ error: "Question is required" }),
        { status: 400, headers: { "Content-Type": "application/json" } }
      );
    }

    const apiKey = process.env.GEMINI_API_KEY;

    if (apiKey && apiKey.length > 20) {
      try {
        const genAI = new GoogleGenerativeAI(apiKey);
        const model = genAI.getGenerativeModel({
          model: "models/gemini-2.0-flash",
          systemInstruction: PORTAL_GUIDE_SYSTEM_PROMPT,
          generationConfig: {
            temperature: 0.3,
            maxOutputTokens: 1000,
          }
        });

        const historyForGemini = (chatHistory || []).map((msg: { role: string; content: string }) => ({
          role: msg.role === "user" ? "user" : "model",
          parts: [{ text: msg.content }],
        }));

        const chat = model.startChat({ history: historyForGemini });
        // Use non-streaming API for better reliability with Next.js (with 45s timeout)
        const result = await withTimeout(
          chat.sendMessage(question),
          45000,
          "‡¶∏‡¶Æ‡¶Ø‡¶º ‡¶∂‡ßá‡¶∑ ‡¶π‡¶Ø‡¶º‡ßá ‡¶ó‡ßá‡¶õ‡ßá, ‡¶Ü‡¶¨‡¶æ‡¶∞ ‡¶ö‡ßá‡¶∑‡ßç‡¶ü‡¶æ ‡¶ï‡¶∞‡ßÅ‡¶®"
        );
        const text = result.response.text();

        // Manually chunk the response for streaming effect
        const encoder = new TextEncoder();
        const readableStream = new ReadableStream({
          async start(controller) {
            const chars = text.split("");
            for (let i = 0; i < chars.length; i++) {
              controller.enqueue(encoder.encode(chars[i]));
              // Small delay for smooth streaming effect
              await new Promise(resolve => setTimeout(resolve, 3 + Math.random() * 5));
            }
            controller.close();
          },
        });

        return new Response(readableStream, {
          headers: {
            "Content-Type": "text/plain; charset=utf-8",
            "Transfer-Encoding": "chunked",
          },
        });
      } catch (apiError) {
        console.error("Gemini API failed, using mock response:", apiError);
        // Fall through to mock response
      }
    }

    // Use mock response (simulated streaming)
    console.log("Using mock portal guide response");
    const mockResponse = generateMockResponse(question);

    const encoder = new TextEncoder();
    const readableStream = new ReadableStream({
      async start(controller) {
        // Simulate streaming by sending character by character
        for (let i = 0; i < mockResponse.length; i++) {
          controller.enqueue(encoder.encode(mockResponse[i]));
          await new Promise(resolve => setTimeout(resolve, 15 + Math.random() * 25));
        }
        controller.close();
      },
    });

    return new Response(readableStream, {
      headers: {
        "Content-Type": "text/plain; charset=utf-8",
        "Transfer-Encoding": "chunked",
      },
    });

  } catch (error) {
    console.error("Portal guide API error:", error);

    return new Response(
      "‡¶¶‡ßÅ‡¶É‡¶ñ‡¶ø‡¶§, ‡¶è‡¶á ‡¶Æ‡ßÅ‡¶π‡ßÇ‡¶∞‡ßç‡¶§‡ßá ‡¶â‡¶§‡ßç‡¶§‡¶∞ ‡¶¶‡¶ø‡¶§‡ßá ‡¶™‡¶æ‡¶∞‡¶õ‡¶ø ‡¶®‡¶æ‡•§ ‡¶Ö‡¶®‡ßÅ‡¶ó‡ßç‡¶∞‡¶π ‡¶ï‡¶∞‡ßá ‡¶Ü‡¶¨‡¶æ‡¶∞ ‡¶ö‡ßá‡¶∑‡ßç‡¶ü‡¶æ ‡¶ï‡¶∞‡ßÅ‡¶®‡•§",
      { headers: { "Content-Type": "text/plain; charset=utf-8" } }
    );
  }
}
